const EXCALIDRAW_TYPES_PREFIX = `These are the typescript types of the elements that can be used to draw elements in Excalidraw: export type Point = [number, number]; type _ExcalidrawElementBase = Readonly<{ id: string; x: number; y: number; strokeColor: string; backgroundColor: string; fillStyle: FillStyle; strokeWidth: number; strokeStyle: StrokeStyle; roundness: null | { type: RoundnessType; value?: number }; roughness: number; opacity: number; width: number; height: number; angle: number; /** Random integer used to seed shape generation so that the roughjs shape doesn't differ across renders. */ seed: number; /** Integer that is sequentially incremented on each change. Used to reconcile elements during collaboration or when saving to server. */ version: number; /** Random integer that is regenerated on each change. Used for deterministic reconciliation of updates during collaboration, in case the versions (see above) are identical. */ versionNonce: number; isDeleted: boolean; /** List of groups the element belongs to. Ordered from deepest to shallowest. */ groupIds: readonly GroupId[]; /** other elements that are bound to this element */ boundElements: | readonly Readonly<{ id: ExcalidrawLinearElement["id"]; type: "arrow" | "text"; }>[] | null; /** epoch (ms) timestamp of last element update */ updated: number; link: string | null; locked: boolean; customData?: Record<string, any>; }>; export type ExcalidrawRectangleElement = _ExcalidrawElementBase & { type: "rectangle"; }; export type ExcalidrawDiamondElement = _ExcalidrawElementBase & { type: "diamond"; }; export type ExcalidrawEllipseElement = _ExcalidrawElementBase & { type: "ellipse"; }; /** * ExcalidrawElement should be JSON serializable and (eventually) contain * no computed data. The list of all ExcalidrawElements should be shareable * between peers and contain no state local to the peer. */ export type ExcalidrawElement = | ExcalidrawGenericElement | ExcalidrawTextElement | ExcalidrawLinearElement | ExcalidrawFreeDrawElement | ExcalidrawImageElement; export type ExcalidrawTextElement = _ExcalidrawElementBase & Readonly<{ type: "text"; fontSize: number; fontFamily: FontFamilyValues; text: string; textAlign: TextAlign; verticalAlign: VerticalAlign; containerId: ExcalidrawGenericElement["id"] | null; originalText: string; /** * Unitless line height (aligned to W3C). To get line height in px, multiply * with font size (using 'getLineHeightInPx' helper). */ lineHeight: number & { _brand: "unitlessLineHeight" }; }>; export type PointBinding = { elementId: ExcalidrawBindableElement["id"]; focus: number; gap: number; }; export type Arrowhead = "arrow" | "bar" | "dot" | "triangle"; export type ExcalidrawLinearElement = _ExcalidrawElementBase & Readonly<{ type: "line" | "arrow"; points: readonly Point[]; lastCommittedPoint: Point | null; startBinding: PointBinding | null; endBinding: PointBinding | null; startArrowhead: Arrowhead | null; endArrowhead: Arrowhead | null; }>; export type ExcalidrawArrowElement = ExcalidrawLinearElement & Readonly<{ type: "arrow"; }>; export type ExcalidrawFreeDrawElement = _ExcalidrawElementBase & Readonly<{ type: "freedraw"; points: readonly Point[]; pressures: readonly number[]; simulatePressure: boolean; lastCommittedPoint: Point | null; }>;`;

